  addClick() {
    this.setState(prevState => ({
      values: [...prevState.values, '']
    }));
    let values = [...this.state.values];
    values.push(this.state.addMoreRow + 1);
    this.setState({
      addMoreRow: this.state.addMoreRow + 1
    });
    this.setState({
      values
    });
  }
  addMoreClick() {
