import { EventEmitter } from "events";

/**
 * Provides retrier service
 */
class Retrier extends EventEmitter {
  private readonly minDelay: number;
  private maxDelay: number;
  private readonly initialDelay: number;
  private readonly maxAttemptsCount: number;
  private readonly maxAttemptsTime: number;
  private readonly randomness: number;

  // fibonacci strategy
  private prevDelay: number;
  private currDelay: number;

  private timeout: ReturnType<typeof setTimeout> | null = null;
  private inProgress: boolean;
  private attemptNum: number;
  private startTimestamp = -1;

  /**
   * Creates a new Retrier instance
   */
  constructor(options: {
    min: number;
    max: number;
    initial?: number;
    maxAttemptsCount?: number;
    maxAttemptsTime?: number;
    randomness?: number;
  }) {
    super();

    this.minDelay = options.min;
    this.maxDelay = options.max;
    this.initialDelay = options.initial || 0;
    this.maxAttemptsCount = options.maxAttemptsCount || 0;
    this.maxAttemptsTime = options.maxAttemptsTime || 0;
    this.randomness = options.randomness || 0;

    this.inProgress = false;
    this.attemptNum = 0;

    this.prevDelay = 0;
    this.currDelay = 0;
  }

  private attempt() {
    if (this.timeout) {
      clearTimeout(this.timeout);
      this.timeout = null;
    }

    this.attemptNum++;
    this.emit("attempt", this);
  }

  private nextDelay(delayOverride?: number): number {
    if (typeof delayOverride === "number") {
      this.prevDelay = 0;
      this.currDelay = delayOverride;
      return delayOverride;
    }

    if (this.attemptNum == 0) {
      return this.initialDelay;
    }

    if (this.attemptNum == 1) {
      this.currDelay = this.minDelay;
      return this.currDelay;
    }

    this.prevDelay = this.currDelay;

    let delay = this.currDelay + this.prevDelay;

    if (this.maxDelay && delay > this.maxDelay) {
      this.currDelay = this.maxDelay;
      delay = this.maxDelay;
    }

    this.currDelay = delay;
    return delay;
  }

  private randomize(delay: number) {
    const area = delay * this.randomness;
    const corr = Math.round(Math.random() * area * 2 - area);
    return Math.max(0, delay + corr);
  }

  private scheduleAttempt(delayOverride?: number) {
    if (this.maxAttemptsCount && this.attemptNum >= this.maxAttemptsCount) {
      this.cleanup();
      this.emit("failed", new Error("Maximum attempt count limit reached"));
      return;
    }

    let delay = this.nextDelay(delayOverride);
    delay = this.randomize(delay);
    if (
      this.maxAttemptsTime &&
      this.startTimestamp + this.maxAttemptsTime < Date.now() + delay
    ) {
      this.cleanup();
      this.emit("failed", new Error("Maximum attempt time limit reached"));
      return;
    }

    this.timeout = setTimeout(() => this.attempt(), delay);
  }

  private cleanup() {
    if (this.timeout) {
      clearTimeout(this.timeout);
      this.timeout = null;
    }
    this.inProgress = false;

    this.attemptNum = 0;
    this.prevDelay = 0;
    this.currDelay = 0;
  }

  public start(): void {
    if (this.inProgress) {
      throw new Error("Retrier is already in progress");
    }

    this.inProgress = true;
    this.startTimestamp = Date.now();
    this.scheduleAttempt(this.initialDelay);
  }

  public cancel(): void {
    if (this.timeout) {
      clearTimeout(this.timeout);
      this.timeout = null;
      this.inProgress = false;

      this.emit("cancelled");
    }
  }

  // @todo Must be a T here, so the entire Retrier must be typed on this value type.
  // eslint-disable-next-line
  public succeeded(arg?: any): void {
    this.emit("succeeded", arg);
  }

  public failed(err: Error, nextAttemptDelayOverride?: number): void {
    if (this.timeout) {
      throw new Error("Retrier attempt is already in progress");
    }

    this.scheduleAttempt(nextAttemptDelayOverride);
  }
}

/**
 * Run retrier as an async function with possibility to await for it.
 * Example:
 * ```
 * const result = AsyncRetrier.run(async () => somePromise);
 * ```
 */
class AsyncRetrier extends EventEmitter {
  private retrier: Retrier;
  // This any must be T typed directly on the AsyncRetrier
  // eslint-disable-next-line
  private resolve: (value: any) => void = () => void 0;
  private reject: (err?: Error) => void = () => void 0;

  constructor(options: {
    min: number;
    max: number;
    initial?: number;
    maxAttemptsCount?: number;
    maxAttemptsTime?: number;
    randomness?: number;
  }) {
    super();
    this.retrier = new Retrier(options);
  }

  public run<T>(handler: () => Promise<T>): Promise<T> {
    this.retrier.on("attempt", () => {
      handler()
        .then((v) => this.retrier.succeeded(v))
        .catch((e) => this.retrier.failed(e));
    });

    this.retrier.on("succeeded", (arg) => this.resolve(arg));
    this.retrier.on("cancelled", () => this.reject(new Error("Cancelled")));
    this.retrier.on("failed", (err) => this.reject(err));

    return new Promise<T>((resolve, reject) => {
      this.resolve = resolve;
      this.reject = reject;

      this.retrier.start();
    });
  }

  public cancel(): void {
    this.retrier.cancel();
  }
}

export { Retrier, AsyncRetrier };
import { EventEmitter } from "events";

function isDef(value: number | undefined | null): value is number {
  return value !== undefined && value !== null;
}

export interface BackoffOptions {
  initialDelay?: number;
  maxDelay?: number;
  randomisationFactor?: number;
  factor?: number;
}

class Backoff extends EventEmitter {
  private readonly maxDelay: number;
  private readonly initialDelay: number;
  private readonly factor: number;
  private readonly randomisationFactor: number;
  private backoffDelay = 0;
  private nextBackoffDelay = 0;
  private backoffNumber = 0;
  private timeoutID: ReturnType<typeof setTimeout> | null = null;
  private maxNumberOfRetry = -1;

  constructor(options: BackoffOptions) {
    super();
    options = options || {};
    const { initialDelay, maxDelay, randomisationFactor, factor } = options;

    if (isDef(initialDelay) && initialDelay < 1) {
      throw new Error(
        "The initial timeout must be equal to or greater than 1."
      );
    }
    if (isDef(maxDelay) && maxDelay <= 1) {
      throw new Error("The maximal timeout must be greater than 1.");
    }
    if (
      isDef(randomisationFactor) &&
      (randomisationFactor < 0 || randomisationFactor > 1)
    ) {
      throw new Error("The randomisation factor must be between 0 and 1.");
    }
    if (isDef(factor) && factor <= 1) {
      throw new Error(`Exponential factor should be greater than 1.`);
    }

    this.initialDelay = initialDelay || 100;
    this.maxDelay = maxDelay || 10000;
    if (this.maxDelay <= this.initialDelay) {
      throw new Error(
        "The maximal backoff delay must be greater than the initial backoff delay."
      );
    }
    this.randomisationFactor = randomisationFactor || 0;
    this.factor = factor || 2;
    this.reset();
  }

  public static exponential(options: BackoffOptions): Backoff {
    return new Backoff(options);
  }

  public backoff(err?: Error): void {
    if (this.timeoutID == null) {
      if (this.backoffNumber === this.maxNumberOfRetry) {
        this.emit("fail", err);
        this.reset();
      } else {
        this.backoffDelay = this.next();
        this.timeoutID = setTimeout(
          this.onBackoff.bind(this),
          this.backoffDelay
        );
        this.emit("backoff", this.backoffNumber, this.backoffDelay, err);
      }
    }
  }

  public reset(): void {
    this.backoffDelay = 0;
    this.nextBackoffDelay = this.initialDelay;
    this.backoffNumber = 0;
    if (this.timeoutID) {
      clearTimeout(this.timeoutID);
    }
    this.timeoutID = null;
  }

  public failAfter(maxNumberOfRetry: number): void {
    if (maxNumberOfRetry <= 0) {
      throw new Error(
        `Expected a maximum number of retry greater than 0 but got ${maxNumberOfRetry}`
      );
    }

    this.maxNumberOfRetry = maxNumberOfRetry;
  }

  next(): number {
    this.backoffDelay = Math.min(this.nextBackoffDelay, this.maxDelay);
    this.nextBackoffDelay = this.backoffDelay * this.factor;
    const randomisationMultiple = 1 + Math.random() * this.randomisationFactor;
    return Math.min(
      this.maxDelay,
      Math.round(this.backoffDelay * randomisationMultiple)
    );
  }

  onBackoff(): void {
    this.timeoutID = null;
    this.emit("ready", this.backoffNumber, this.backoffDelay);
    this.backoffNumber++;
  }
}

export { Backoff };
export default Backoff;
